import React from "react";
import { Link, NavLink, useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import "./Style/ReadBooks.css";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";
const ReadBooks = () => {
  let params = useParams();
  let id = params.id;

  let [userData, setUserData] = useState({});
  useEffect(() => {
    fetch(`http://localhost:4000/books/${id}`)
      .then((res) => res.json())
      .then((data) => setUserData(data));
  }, [userData]);

  let { longDescription, title, thumbnailUrl, shortDescription } = userData;

  //   !===========
  let [msg, setMsg] = useState(true);
  let [show, setShow] = useState(true);

  let Show = () => {
    setMsg(!msg);
  };
  let long = () => {
    setShow(!show);
  };
  
  
useLo

  let navigate =useNavigate()
  let returnback=()=>{
    path?
    navigate('/adminportal/books')
    :
    navigate('/usersportal/books')
}

  return (
    <div className="bookss">
      <div className="cardd">
        <div className="booksphoto">
          <img src={thumbnailUrl} alt="" />
        </div>
        <div className="all">
          <div className="readtitle">Title : {title}</div>

          <button className="shortdesc">
            <div className="hii">
              <div>Short Description</div>
              <div className="eye" onClick={Show}>
                {msg ? (
                  <div title="Click to See">
                    <RemoveRedEyeIcon />{" "}
                  </div>
                ) : (
                  <div title="Click to Dont See">
                    <VisibilityOffIcon />
                  </div>
                )}
              </div>
            </div>
            {msg ? setMsg : <p>{shortDescription}</p>}
          </button>
          <button className="longdesc">
            <div className="hii">
              <div>Long Description</div>
              <div className="eye" onClick={long}>
                {show ? (
                  <div title="Click to See">
                    <RemoveRedEyeIcon />
                  </div>
                ) : (
                  <div title="Click to don't See">
                    {" "}
                    <VisibilityOffIcon />
                  </div>
                )}
              </div>
            </div>
            {show ? setShow : <p>{longDescription}</p>}
          </button>

          <div className="back">
            <button onClick={returnBack}>Back to Book</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReadBooks;
