import React from "react";
import {
  Link,
  NavLink,
  useLocation,
  useNavigate,
  useParams,
} from "react-router-dom";
import { useEffect, useState } from "react";
import "./Style/ReadBooks.css";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import VisibilityOffIcon from "@mui/icons-material/VisibilityOff";
const ReadBooks = () => {
  let params = useParams();
  let id = params.id;

  let [userData, setUserData] = useState({});
  useEffect(() => {
    fetch(`http://localhost:4000/books/${id}`)
      .then((res) => res.json())
      .then((data) => setUserData(data));
  }, [userData]);

  let { longDescription, title, thumbnailUrl, shortDescription } = userData;

  //   !===========
  let [msg, setMsg] = useState(true);
  let [show, setShow] = useState(true);

  let Show = () => {
    setMsg(!msg);
  };
  let long = () => {
    setShow(!show);
  };

  let location = useLocation();
  let path = location.pathname.startsWith("/adminportal");

  let navigate = useNavigate();
  let returnback = () => {
    path ? navigate("/adminportal/book") : navigate("/userportal/book");
  };

  // !==================

  let [cart, setCart] = useState([]);
  useEffect(() => {
    fetch("http://localhost:4000/cart")
      .then((response) => response.json())
      .then((data) => setCart(data));
  }, [cart]);

  useEffect(() => {
    cart.map((elem) => setId(elem.id));
  }, [cart]);

  let addToCart = () => {
    console.log(id);

