import React from "react";
import { Link, NavLink, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import "./Style/ReadBooks.css";
import ArrowCircleUpIcon from "@mui/icons-material/ArrowCircleUp";
import ArrowCircleDownIcon from "@mui/icons-material/ArrowCircleDown";
import CloseIcon from '@mui/icons-material/Close';
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
const ReadBooks = () => {
  let params = useParams();
  let id = params.id;

  let [userData, setUserData] = useState({});
  useEffect(() => {
    fetch(`http://localhost:4000/books/${id}`)
      .then((res) => res.json())
      .then((data) => setUserData(data));
  }, [userData]);

  let { longDescription, title, thumbnailUrl, shortDescription } = userData;

  //   !===========
  let [msg, setMsg] = useState(true);
  let [show, setShow] = useState(true);

  let Show = () => {
    setMsg(!msg);
  };
  let long = () => {
    setShow(!show);
  };
  return (
    <div className="bookss">
      <div className="cardd">
        <div className="booksphoto">
          <img src={thumbnailUrl} alt="" />
        </div>
        <div className="all">
          <div className="readtitle">Title : {title}</div>

          <button className="shortdesc" onClick={Show}>
            <div className="hii">
              <div>Short Description</div>
              <div>{msg ? <RemoveRedEyeIcon /> : <VisibilityOffIcon />}</div>
            </div>
            {msg ? setMsg : <p>{shortDescription}</p>}
          </button>
          <button className="longdesc" onClick={long}>
            <div className="hii">
              <div>Long Description</div>
              <div>{msg ? <RemoveRedEyeIcon /> : <ArrowCircleDownIcon />}</div>
            </div>
            {show ? setShow : <p>{longDescription}</p>}
          </button>

          <div className="back">
            <NavLink to="/adminportal/book"> Back to Book</NavLink>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReadBooks;
