import React, { useState } from "react";

const ShortDes = (props) => {
  let [msg, setMsg] = useState(true);
  let show = () => {
    setMsg(!msg);
  };

  let { short } = props;
  return (
    <div>
      <button onClick={show}>shortDescription</button>
      <h3>{short}</h3>
    </div>
  );
};

export default ShortDes;
