import React, { useEffect, useRef, useState } from "react";
import "./style/Shopping.css";
const Shopping = () => {
  // !================

  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  // !=============
  let Cart = useRef();
  let SideBar = useRef();
  let shopping = useRef();

  let [bool, setBool] = useState(true);

  let barOpen = () => {
    SideBar.current.classList.toggle("open");
    shopping.current.classList.toggle("open");
  };
  let cartOpen = () => {
    Cart.current.classList.toggle("open");
  };

  // !======================= Code

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  let LoadCategories = () => {
    fetch("http://fakestoreapi.com/products/categories")
      .then((res) => res.json())
      .then((categories) => {
        categories.unshift("all");
        setCategories(categories);
      });
  };

  let LoadProducts = (url) => {
    fetch(url)
      .then((res) => res.json())
      .then((products) => {
        setProducts(products);
      });
  };

  useEffect(() => {
    LoadCategories();
    LoadProducts("http://fakestoreapi.com/products");
  }, []);

  let handleCategoryChange = (e) => {
    if (e.target.value === "all") {
      LoadProducts("http://fakestoreapi.com/products");
    } else {
      LoadProducts(
        `http://fakestoreapi.com/products/category/${e.target.value}`
      );
    }
  };

  // !===================
  const [cartCount, setCartCount] = useState(0);
  const [cartItems, setCartItems] = useState([]);

  function handleAddToCartClick(e) {
    fetch(`http://fakestoreapi.com/products/${e.target.value}`)
      .then((res) => res.json())
      .then((product) => {
        cartItems.push(product);
        setCartCount(cartItems.length);
        alert(`${product.title}\nAdded to Cart`);
      });
  }

  let removeCart = (e) => {
    fetch(`http://fakestoreapi.com/products/${e.target.value}`)
      .then((res) => res.json())
      .then((elem) => {
        setCartCount(cartItems.length - 1);
        cartItems.pop(elem);
      });
  };

  return (
    <>
      <nav className="navbar">
        <div className="nav-logo">
          <i className="fa fa-bars"></i>
          <div>
            <i className="fa fa-user"></i> MyLogo
          </div>
        </div>
        <h1 onClick={barOpen}>Shopping</h1>
        <div className={`nav-links ${isOpen ? "open" : ""}`}>
          <h3 onClick={cartOpen}>Cart {cartCount}</h3>
        </div>
        <div className="nav-toggle" onClick={toggleMenu}>
          <i className="fa fa-bars"></i>
        </div>
      </nav>
      <div className="main">
        <div ref={SideBar} className="sideBar">
          <div>
            <label className="">Select Category</label>
            <div>
              <select onChange={handleCategoryChange} className="form-select">
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category.toUpperCase()}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        <div ref={shopping} className="shopping">
          {products.map((elem) => {
            return (
              <>
                <div className="card ">
                  <div className="name "> {}</div>
                  <div className="image">
                    <img src={elem.image} alt="Image is Not Yet" />
                  </div>
                  <div className="year">Authors : {}</div>

                  <div className="buttons">
                    <button className="buttons1" onClick={() => {}}>
                      Buy Now
                    </button>
                    <button
                      value={elem.id}
                      onClick={handleAddToCartClick}
                      className="buttons2"
                    >
                      Add to Cart
                    </button>
                  </div>
                </div>
              </>
            );
          })}
        </div>
        <div ref={Cart} className="cart">
          <table border={1}>
            <caption> Your Cart Summary</caption>
            <thead>
              <tr>
                <th>Title</th>
                <th>Preview</th>
                <th>Remove</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr>
                  <td>{item.title}</td>
                  <td>
                    <img src={item.image} width="50" height="50" />
                  </td>
                  <td>
                    <button value={item.id} onClick={removeCart}>
                      remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default Shopping;
