import React, { useRef, useState } from "react";
import "./style/Shopping.css";
const Shopping = () => {
  // !================

  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  // !=============
  let Cart = useRef();
  let SideBar = useRef();
  let shopping = useRef();

  let [bool, setBool] = useState(true);

  let barOpen = () => {
    SideBar.current.classList.toggle("open");
    shopping.current.classList.toggle("open");
  };
  let cartOpen = () => {
    Cart.current.classList.toggle("open");
  };
  return (
    <>
      <nav className="navbar">
        <div className="nav-logo">
          <i className="fa fa-user"></i> MyLogo
        </div>
        <div className={`nav-links ${isOpen ? "open" : ""}`}>
          <h1>Shopping</h1>
          <h3 onClick={cart}>Cart</h3>
        </div>
        <div className="nav-toggle" onClick={toggleMenu}>
          <i className="fa fa-bars"></i>
        </div>
      </nav>
      <div className="main">
        <div ref={SideBar} className="sideBar"></div>
        <div ref={shopping} className="shopping">
          <button onClick={barOpen}>SideBar</button>
          <button onClick={cartOpen}>Cart</button>
        </div>
        <div ref={Cart} className="cart"></div>
      </div>
    </>
  );
};

export default Shopping;
