import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Style/Books.css";
const Books = () => {
  let [book, setBook] = useState([]);
  let fetchApi = async () => {
    let apiData = await fetch("http://localhost:4000/books")
    // .then((res) =>
    //   res.json()
    // );

    let b =await apiData.json()
   await setBook(b);
   console.log(b)
  };
  fetchApi();

  // !==========================
// console.log(book.id)
let navigate = useNavigate()

let readBook =async ()=>{
//  await navigate(`/adminportal/readbooks/${book.id}`)
await console.log(book.title)
}
  return (
    <>
      <div className="books">
        {book.map((elem) => {
          let {
            id,
            title,
            isbn,
            pageCount,
            publishedDate,
            thumbnailUrl,
            longDescription,
            status,
            authors,
            categories,
          } = elem;

          return (
            <>
              <div className="card ">
                <div className="name "> {title}</div>
                <div className="image">
                  <img src={thumbnailUrl} alt="Image is Not Yet" />
                </div>
                <div className="year">Authors : {authors[0]}</div>
                <div className="year1">Page Count :{pageCount}</div>
                <div className="year1 a">Reg No : {isbn}</div>
                <div className="year1 b">Status :{status}</div>
                <div className="year1 c">Categories : {categories[0]}</div>
                <div className="buttons">
                  <button className="buttons1" onClick={readBook}>Read Books</button>
                  <button className="buttons2">Delete</button>
                </div>
              </div>
            </>
          );
        })}
      </div>
    </>
  );
};

export default Books;
