import React, { useEffect, useRef, useState } from "react";
import "./style/Shopping.css";
const Shopping = () => {
  // !================

  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  // !=============
  let Cart = useRef();
  let SideBar = useRef();
  let shopping = useRef();

  let [bool, setBool] = useState(true);

  let barOpen = () => {
    SideBar.current.classList.toggle("open");
    shopping.current.classList.toggle("open");
  };
  let cartOpen = () => {
    Cart.current.classList.toggle("open");
  };

  // !======================= Code

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  let LoadCategories = async () => {
    await fetch("http://fakestoreapi.com/products/categories")
      .then((res) => res.json())
      .then((categories) => {
        categories.unshift("all");
        setCategories(categories);
      });
  };
  LoadCategories();

  let handleCategoryChange = async (e) => {
    if (e.target.value === "all") {
      await LoadProducts("http://fakestoreapi.com/products");
    } else {
      LoadProducts(
        await `http://fakestoreapi.com/products/category/${e.target.value}`
      );
    }
  };

  let LoadProducts = async (url) => {
    await fetch(url)
      .then((res) => res.json())
      .then((products) => {
        setProducts(products);
      });
  };
  useEffect(() => {
    LoadCategories();
    LoadProducts("http://fakestoreapi.com/products");
  }, []);
  return (
    <>
      <nav className="navbar">
        <div className="nav-logo">
          <i className="fa fa-user"></i> MyLogo
        </div>
        <h1 onClick={barOpen}>Shopping</h1>
        <div className={`nav-links ${isOpen ? "open" : ""}`}>
          <h3 onClick={cartOpen}>Cart</h3>
        </div>
        <div className="nav-toggle" onClick={toggleMenu}>
          <i className="fa fa-bars"></i>
        </div>
      </nav>
      <div className="main">
        <div ref={SideBar} className="sideBar">
          <div>
            <label className="">Select Category</label>
            <div>
              <select onChange={handleCategoryChange} className="form-select">
                {categories.map((category) => (
                  <option key={category} value={category}>
                    {category.toUpperCase()}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
        <div ref={shopping} className="shopping">
          {products.map((elem) => {
            return (
              <>
                <div className="books">
        {book.map((elem) => {
          let {
            id,
            title,
            isbn,
            pageCount,
            thumbnailUrl,
            status,
            authors,
            categories,
          } = elem;

          return (
            <>
              <div className="card ">
                <div className="name "> {title}</div>
                <div className="image">
                  <img src={thumbnailUrl} alt="Image is Not Yet" />
                </div>
                <div className="year">Authors : {authors[0]}</div>
                <div className="year1">Page Count :{pageCount}</div>
                <div className="year1 a">Reg No : {isbn}</div>
                <div className="year1 b">Status :{status}</div>
                <div className="year1 c">Categories : {categories[0]}</div>
                <div className="buttons">
                  <button
                    className="buttons1"
                    onClick={() => {
                      readBook(id);
                    }}
                  >
                    Read Books
                  </button>
                  <button
                    onClick={() => deleteBook(id, title)}
                    className="buttons2"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </>
          );
        })}
      </div>
              </>
            );
          })}
        </div>
        <div ref={Cart} className="cart"></div>
      </div>
    </>
  );
};

export default Shopping;
