    }
    SideBar.current.style.left ="0"
  };
  let cartOpen = () => {
    Cart.current.style.right ="0"
  };
  return (
    <div className="main">
      <div ref={SideBar} className="sideBar"></div>
      <div ref={shopping} className="shopping">
        <button onClick={barOpen}>SideBar</button>
        <button onClick={cartOpen}>Cart</button>
      </div>
      <div ref={Cart} className="cart"></div>
    </div>
  );
};

export default Shopping;
