import React, { useState } from "react";
import { useRef } from "react";

const AddUsers = () => {
  let date = new Date();
  let fnm = useRef();
  let lnm = useRef();
  let email = useRef();
  let place = useRef();
  let dob = useRef();

  let [users, setUsers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/users")
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    let newUser = {
      id: users.length + 1,
      FirstName: fnm.current.vlue,
      LastName: lnm.current.vlue,
      Email: email.current.vlue,
      State: place.current.vlue,
      DOB: dob.current.vlue,
      Time: date,
    };

    fetch(`http://localhost:4000/users`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser),
    });
  };
  return (
    <div className="addBooks">
      <form onSubmit={handleSubmit}>
        <input ref={fnm} type="text" placeholder="Enter First Name" />
        <input ref={lnm} type="text" placeholder="Enter Last Name" />
        <input ref={email} type="email" placeholder="Enter email Address" />
        <input ref={place} type="text" placeholder="Enter Place" />
        <input ref={dob} type="date" placeholder="Date of Birth" />
        <button>Add Users</button>
      </form>
    </div>
  );
};

export default AddUsers;
