import React, { useRef } from "react";
import { Link, useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import "./Style/ReadBooks.css";
const ReadBooks = () => {
  let params = useParams();
  let id = params.id;

  let [userData, setUserData] = useState({});
  useEffect(() => {
    fetch(`http://localhost:4000/books/${id}`)
      .then((res) => res.json())
      .then((data) => setUserData(data));
  }, [userData]);

  let { longDescription, title, thumbnailUrl, shortDescription } = userData;

  //   !===========
  let [msg, setMsg] = useState(true);
  let [show, setShow] = useState(true);
  let hello = useRef();
  let Show = () => {
    setMsg(!msg);
   

    if ((hello.current.innerText = null)) {
      alert("nei");
    }
  };
  let long = () => {
    setShow(!show);
  };
  return (
    <div className="bookss">
      <div className="cardd">
        <div className="booksphoto">
          <img src={thumbnailUrl} alt="" />
        </div>
        <div className="all">
          <div className="readtitle">Title : {title}</div>

          <button className="shortdesc" onClick={Show}>
            <div className="hii">shortDescription</div>
            {msg ? setMsg : <p ref={hello}>{shortDescription}</p>}
          </button>
          <button className="longdesc" onClick={long}>
            <div className="hii">shortDescription</div>
            {show ? setShow : <p>{longDescription}</p>}
          </button>

          <div className="back">
            <Link to="/adminportal/book"> Back to Book</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReadBooks;
