import React, { useRef } from "react";
import "./contact.css";
import facebook from ".././assetss/facebook.png";
import twitter from ".././assetss/twitter.png";
import instagram from ".././assetss/instagram.png";
import linkedin from ".././assetss/linkedin.png";
import emailjs from "@emailjs/browser";
export function Contact() {
  const form = useRef();
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_1dow99z",
        "template_4lpd4ox",
        form.current,
        "sApXq7QvvWWYFOrZ34UoU"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <section id="contactPage">
      <div id="contact">
        <h1 className="contactPageTitle">Contact Me</h1>
        <span className="contactDecs">
          Please fill out the form below to discuss any work opportunities.
        </span>
        <form className="contactForm" ref={form} onSubmit={sendEmail}>
          <input
            type="text"
            className="name"
            placeholder="Your Name"
            name="your_name"
          />
          <input
            type="text"
            className="email"
            placeholder="Your Email"
            name="your_email"
          />
          <textarea
            name="message"
            rows="5"
            className="msg"
            placeholder="Your Message"
          ></textarea>
          <button type="submit" value="Send" className="submitBtn">
            Submit
          </button>
          <div className="links">
            <img src={facebook} alt="Facebook" className="link" />
            <img src={twitter} alt="twitter" className="link" />
            <img src={instagram} alt="Youtube" className="link" />
            <img src={linkedin} alt="Instagram" className="link" />
          </div>
        </form>
      </div>
    </section>
  );
}
