import React from "react";
import { useState } from "react";
import "./UseStateProject.css";

const UseStateProject = () => {
  let persons = [
    { id: 1, fname: "Abhi", lname: "Ram", age: 50 },
    { id: 2, fname: "Darshan", lname: "JS", age: 45 },
    { id: 3, fname: "Abhijeet", lname: "Singh", age: 69 },
  ];

  let [users, setUsers] = useState(persons);
  // console.log(users)

  let viewUser = (lname, age) => {
    alert(`${lname} is ${age}`);
  };

  let deleteUser = (id, lname) => {
    // let x=users.filter((elem)=>{
    //     return elem.id !== id
    // })
    // setUsers([...x])

    let bool = window.confirm(`Do you want to delete ${lname} data...?`);

    if (bool) {
      setUsers([...users.filter((elem) => elem.id !== id)]);
      alert(`${lname} is deleted`);
    } else {
      alert(`${lname} data is not deleted`);
    }
  };
  let deleteAllUser = () => {
    let bool = window.confirm(`Do you want to delete all users`);
    bool ? setUsers([]) : alert("Data is not deletd");
  };

  let addUsers = () => {
    let fnm = prompt("Enter First Name");
    let lnm = prompt("Enter Last Name");
    let age = prompt("Enter Age");

    setUsers ([])
  };
  return (
    <div>
      <div className="arrayObject">
        <div className="header">Users Details</div>
        <table border={1}>
          <thead>
            <tr>
              <th>Sl No</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th>View</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {users.map((elem, index) => {
              let { id, fname, lname, age } = elem;

              return (
                <tr>
                  <th>{index + 1}</th>
                  <th>{fname}</th>
                  <th>{lname}</th>
                  <th>{age}</th>
                  <th>
                    <button onClick={() => viewUser(lname, age)}>View</button>
                  </th>
                  <th>
                    <button onClick={() => deleteUser(id, lname)}>
                      Delete
                    </button>
                  </th>
                </tr>
              );
            })}

            {users.length !== 0 ? (
              <tr>
                <th colSpan={6}>
                  <button onClick={() => deleteAllUser()}>Delete All</button>
                </th>
              </tr>
            ) : (
              <tr>
                <th colSpan={6}>
                  <button onClick={() => addUsers()}>Add User </button>
                </th>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UseStateProject;
