import React from "react";
import { useState } from "react";

const Forms = () => {
  const [user, setUser] = useState({ Name: "", Age: 0, Mobile: "", City: "" });

  let handleNameChange = (e) => {
    setUser({
      Name: e.target.value,
      Age: user.Age,
      Mobile: user.Mobile,
      City: user.City,
    });
  };

  let handleAgeChange = (e) => {
    setUser({
      Name: user.Name,
      Age: e.target.value,
      Mobile: user.Mobile,
      City: user.City,
    });
  };

  let handleMobileChange = (e) => {
    setUser({
      Name: user.Name,
      Age: user.Age,
      Mobile: e.target.value,
      City: user.City,
    });
  };

  let handleCityChange = (e) => {
    setUser({
      Name: user.Name,
      Age: user.Age,
      Mobile: user.Mobile,
      City: e.target.value,
    });
  };

  let handleSubmitClick = () => {
    alert(JSON.stringify(user));
  };
  return (
    <div>
      <div className="container-fluid d-flex justify-content-center border border-5 border-primary w-50 mt-5 p-5 bg-primary text-bg-danger text-center  ">
        <form onSubmit={handleSubmitClick}>
          <h3>Register User</h3>
          <dl>
            <dt>User Name</dt>
            <dd>
              <input type="text" onChange={handleNameChange} />
            </dd>
            <dt>Age</dt>
            <dd>
              <input type="number" onChange={handleAgeChange} />
            </dd>
            <dt>Mobile</dt>
            <dd>
              <input type="text" onChange={handleMobileChange} />
            </dd>
            <dt>City</dt>
            <dd>
              <select onChange={handleCityChange}>
                <option value="-1">Select City</option>
                <option value="Delhi">Delhi</option>
                <option value="Hyd">Hyd</option>
              </select>
            </dd>
          </dl>
          <button>Submit</button>
          <h1 className="text-danger mt-5">{user.Name}</h1>
          <h1 className="text-danger ">{user.age}</h1>
          <h1 className="text-danger ">{user.Name}</h1>
          <h1 className="text-danger mt-5">{user.Name}</h1>
        </form>
      </div>
    </div>
  );
};

export default Forms;
