import React from "react";
import { useState } from "react";

const UseState = () => {
  const [bool, setBool] = useState(true);
  let toggolBtn = () => {
    setBool(!bool);
   
  };
  return (
    <>
      <div>
        <button onClick={toggolBtn}>Click</button>
      </div>
    </>
  );
};

export default UseState;
