import React, { useState } from "react";
import mcqData from "./data";
import ToggleQue from "./ToggleQue";
import "./mcq.css";

const QueAns = () => {

  let [msg, setMsg] = useState(true);

  let Show = () => {
    setMsg(!msg);
  };
  return (
    <>
      <div className="mcq">
        <div className="header">Mcq Questions</div>
        <div>
          {mcqData.map((elem) => {
            let { id, que, ans } = elem;
            return (
              <>
                <ToggleQue id={id} que={que} ans={ans} />

                <div>
                  <button onClick={Show} className="btn btn-primary">
                    <div> {que}</div>{" "}
                    <div>
                      {" "}
                      {/* {msg ? <ArrowCircleDownIcon /> : <ArrowCircleUpIcon />} */}
                    </div>{" "}
                    {msg ? setMsg : <p>{ans}</p>}
                  </button>
                </div>
              </>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default QueAns;
