import "./login.css";

export function Login() {
  let loginPage = (e) => {
    e.preventDefault();
    console.log(e);
    console.log(e.target);

    let user = e.target[0];
    let pswd = e.target[1];

    if (user.value === "Sourav Kumar Dash" && pswd.value === "2002") {
     alert(`User id : ${user.value} `)
    } else if (user.value === "Sourav Kumar Dash" && pswd.value === "2002") {
      window.location = "home.html";
    } else {
      let err = "border: 2px solid red";
      user.style.cssText = err;
      pswd.style.cssText = err;
      window.alert("Incorrect password");
    }
  };

  return (
    <div className="container" onSubmit={loginPage}>
      <form>
        <h2>User Login</h2>
        <dl>
          <dt>Customer Id</dt>
          <dd>
            <input type="text" />
          </dd>
          <dt>Password</dt>
          <dd>
            <input type="password" />
          </dd>
        </dl>
        <button className="btn btn-danger">Login</button>
        <button className="btn btn-primary">Cancel</button>
      </form>
    </div>
  );
}
