import React, { useRef } from "react";

const FormUseRef = () => {
  let form= useRef();


  let handleSubmit = (e) => {
    e.preventDefault();
    console.log(fnm.current.value);
    console.log(lnm.current.value);
  };
  return (
    <>
      <div className="useRef">
        <div className="header text-center text-bg-danger text-primary display-6 mb-5">
          Print Data
        </div>
        <div className="container text-center">
          <form
            onSubmit={handleSubmit}
            action=""
            className="border p-5 border-3"
          >
            <input
              ref={fnm}
              type="text"
              placeholder="Enter First Name"
              className="form-control mb-3"
            />
            <input
              ref={lnm}
              type="text"
              placeholder="Enter Last Name"
              className="form-control mb-3"
            />
            <button className="btn btn-danger ">Print Data</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default FormUseRef;
