import React, { useRef } from "react";

const FirstUseRef = () => {
  let msg = useRef();

  let changeMsg = () => {
    msg.current.innerText = "ReactJs";
    msg.current.style.cssText = "color:red";
    msg = !msg;
  };

  return (
    <>
      <div className="useref">
        <div className="hrader text-center">Change Content</div>
        <h1 ref={msg}>{msg ? "javascript":"HEllo"}</h1>
        <button onClick={changeMsg} className="btn btn-primary">
          Click Here
        </button>
      </div>
    </>
  );
};

export default FirstUseRef;
