import React from "react";
import MoviesData from "./MoviesData";
import "./Movies.css";

const Movies = () => {
  return (
    <>
      <div className="header">Movies Gallary</div>
      <div className="d-flex flex-wrap justify-content-center mt-5 ">
        {MoviesData.map((elem) => (
          <div className="card  m-2 " style={{ width: "32vh", height: "70vh" }}>
            <div className="name p-2">Movies -{elem.name}</div>
            <div className="image">
              <img src={elem.imgUrl} alt="" />
            </div>
            <hr />
            <div className="d-flex justify-content-center text-uppercase all">
              Released-{elem.Released}
            </div>
            <div className="d-flex justify-content-center text-uppercase all">
              Language-{elem.Language}
            </div>
            <div className="d-flex justify-content-center text-uppercase all">
              Hero-{elem.Hero}
            </div>
            <div className="d-flex justify-content-center text-uppercase all">
              budget-{elem.budget}
            </div>
            <hr />
            <div className="d-flex justify-content-center pb">
              <button className="btn btn-danger w-50 d-flex justify-content-center">
                {" "}
                Watch
              </button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Movies;
