import React, { useState } from "react";

const ToggleQue = (props) => {
  let [msg, setMsg] = useState(true);

  let Show = () => {
    setMsg(!msg);
  };

  let { id, que, ans } = props;
  return (
    <>
      <div>
        <button onClick={Show} className="btn btn-primary"> {que}</button>
        {msg ? setMsg : <p>{ans}</p>}
      </div>
    </>
  );
};

export default ToggleQue;
