import "./basic.css";
import { useState } from "react";

let BasicJsx = () => {
  let fname = "Dinga";
  let lname = "Raja";
  let num = 12;
  let multiple = num * num;

  const [otp , setopt]=useState(root)

  let root = document.getElementById("root");
  let generateOtp = () => {
    let a = Math.floor(Math.random() * 9000 + 1000);
    root.innerText = a;
  };

  // let b = setInterval(generateOtp, 1000);
  return (
    <>
      {/* Static Content */}
      <h1> Welcome to React class</h1>

      {/* Static with Dynamic Content */}
      <h3>
        My name is {fname} {lname}
      </h3>

      {/* Dynamic Content */}
      <h3>
        {num} * {num} = {num * num}
      </h3>
      <h3>
        {num} * {num} = {multiple}
      </h3>

      <button onClick={generateOtp}> Get OTP</button>
      <p id="root" className="hello">
        OTP
      </p>
    </>
  );
};
export default BasicJsx;
