import React, { useState } from 'react';
// import { Redirect } from 'react-router-dom'; // Assuming you're using React Router for navigation

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleLogin = () => {
    // You can add your authentication logic here, for now let's assume some hardcoded values
    if (username === 'admin' && password === 'password') {
      // Mock user data, replace this with your actual user data
      const mockUserData = {
        name: 'John Doe',
        email: 'john@example.com',
        // Add more user details here
      };
      setUserData(mockUserData);
      setLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  if (loggedIn) {
    return <Redirect to="/profile" />;
  }

  return (
    <div>
      <h2>Login Page</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Username:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
