const FirstUseRef = () => {
  let msg = useRef;
  console.log(msg)

  return (
    <>
      <div className="useref">
        <div className="hrader text-center">Change Content</div>
        <h1 ref={msg.current}>Java Script</h1>
        <button className="btn btn-primary">Click Here</button>
      </div>
    </>
  );
};

export default FirstUseRef;
