import React from "react";
import { useState } from "react";

const Event = () => {
  const [userName, setUserName] = useState("John");

  function handleNameChange(event) {
    setUserName(event.target.value);
  }

  const printName = () => {
    let fname = "Dinga";
    let lname = "raja";
    alert(`My name is ${fname} ${lname}`);
  };

  const print = (msg) => {
    alert(msg);
  };

  let root = document.getElementById("hello")
  let generateOtp = () => {
    let a = Math.floor(Math.random() * 9000 + 1000);
    alert(`OTP is ${a}`);
    root.innerHTML += a
  };
  return (
    <>
      <div className="Events">
        <div className="header ">
          <div>
            <button
              onClick={() => alert("Welcome")}
              className="btn btn-primary  m-5 col-5"
            >
              {" "}
              Click Here
            </button>

            <button onClick={printName} className="btn btn-primary  m-5 col-5">
              {" "}
              Print Name
            </button>
            <button
              onClick={() => print("Good evening")}
              className="btn btn-primary  m-5 col-5"
            >
              {" "}
              Good Evening
            </button>
            <button
              onClick={generateOtp}
              className="btn btn-primary  m-5 col-5"
            >
              {" "}
              Generate OTP
            </button>
            <div id="hello"></div>
          </div>
        </div>
      </div>

      <div className=" m-5 ">
        <dl>
          <dt>User Name</dt>
          <dd>
            <input type="text" value={userName} onChange={handleNameChange} />
          </dd>
        </dl>
        <p>Hello ! {userName} </p>
      </div>
    </>
  );
};

export default Event;
