  //   };

  //   !onClick Show Paragraph
  const [msg, showMsg] = useState(true);
  let ShowMsg = () => {
    showMsg(!msg);
  };

  return (
    <>
      <div>
        {/* <button onClick={toggolBtn}>Click</button> */}
        <div className="text-center">
          <button className="btn btn-primary " onClick={ShowMsg}>
            {msg ? "Show Para" : "Hide Para"}
