import React, { useRef } from "react";
import "./Style.css";
const AddForm = () => {
  let imgUrl = useRef();
  let fname = useRef();
  let lname = useRef();
  let dob = useRef();

  let timgUrl = useRef();
  let tfname = useRef();
  let tlname = useRef();
  let tdob = useRef();

  let handleSubmit = (e) => {
    e.preventDefault();

    timgUrl.current.innerText = imgUrl.current.value;
    tfname.current.innerText = fname.current.value;
    tlname.current.innerText = lname.current.value;
    tdob.current.innerText = dob.current.value;
  };

  return (
    <div>
      <div className="formDetails">
        <form onSubmit={handleSubmit}>
          <input ref={imgUrl} type="text" placeholder="Enter Image Url" />
          <input ref={fname} type="text" placeholder="Enter First Name" />
          <input ref={lname} type="text" placeholder="Enter Last Name" />
          <input ref={dob} type="number" placeholder="Enter DOB" />
          <input type="submit" />
        </form>
      </div>
      <div className="table-details">
        <table border={1}>
          <tr>
            <th>Sl no</th>
            <th>Fname</th>
            <th>Lname</th>
            <th>Dob</th>
            <th>Age</th>
            <th>Photo</th>
          </tr>
          <tr>
            <th>1</th>
            <th ref={tfname}></th>
            <th ref={tlname}></th>
            <th ref={tdob}></th>
            <th ref={timgUrl}></th>
            <th></th>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default AddForm;
