import React from "react";
import MoviesData from "./MoviesData";
import "./Movies.css";

const Movies = () => {
  return (
    <>
      <div className="header">Movies Gallary</div>
      <div
        className="d-flex flex-wrap overflow-auto"
        style={{ height: "750px" }}
      >
        {MoviesData.map((product) => (
          <div className="card p-2 m-2 " style={{ width: "200px" }}>
            <img src={product.image} className="card-img-top" height="140" />
            <div
              className="card-header overflow-auto"
              style={{ heigh: "140px" }}
            >
              {product.title}
            </div>
            <div className="card-body">
              <dl>
                <dt>Price</dt>
                <dt>
                  {product.price.toLocaleString("en-IN", {
                    style: "currency",
                    currency: "INR",
                  })}
                </dt>
              </dl>
            </div>
            <div className="card-footer">
              <button
                value={product.id}
                
                className="btn btn-danger w-100"
              >
                <span className="bi bi-cart3"></span>Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Movies;
