import React, { useEffect, useRef, useState } from "react";
import "./movies.css";
const MovieApi = () => {
  let searchField = useRef();
  // !============================================================
  //   useEffect(() => {
  //     fetch("http://www.omdbapi.com/?s=animal&apikey=273b18f0")
  //       .then((res) => res.json())
  //       .then((data) => console.log(data));
  //   }, []);
  // !============================================================
  let [movie, setMovie] = useState([]);

  let handleSubmit = (e) => {
    e.preventDefault();
    let inputData = searchField.current.value;

    let fetchApi = () => {
      fetch(`http://www.omdbapi.com/?s=${inputData}&apikey=273b18f0`)
        .then((data) => data.json())
        .then((elem) => setMovie(elem.Search));
    };
    fetchApi();
  };

  return (
    <>
      <div className="movieapi">
        <div className="header">Search Movies</div>

        <div>
          <form onSubmit={handleSubmit}>
            <input className="container form-control"
              type="text"
              placeholder="Enter Your Movies Name"
              ref={searchField}
            />
            <button>Submit</button>
{/* 
            {movie.map((data) => {
              let { Title, Poster, Year } = data;
              return (
                <>
                  <img src={Poster} alt="" />
                  <div>{Title}</div>
                  <div>{Year}</div>
                </>
              );
            })} */}


            {

            }
          </form>
        </div>
      </div>
    </>
  );
};

export default MovieApi;
