import React, { useState } from "react";
import mcqData from "./data";
import "./mcq.css";
const QueAns = () => {
  let [bool, setAns] = useState(true);

  let seeAns = () => {
    setAns(!ans);
  };
  return (
    <>
      <div className="mcq">
        <div className="header">Mcq Questions</div>
        <div>
          {mcqData.map((elem) => {
            let { id, que, ans } = elem;
            return (
              <>
                <button className="btn btn-primary" onClick={seeAns}>
                  {que}

                  <div>{ans ? "" : <p>{ans}</p>}</div>
                </button>
              </>
            );
          })}
        </div>
      </div>
    </>
  );
};

export default QueAns;
