import React, { useEffect, useState } from "react";

const UseEffect = () => {
  let [count, setNum] = useState(0);

  const Incrise = () => {
    setNum(count + 1);
  };
  const Decre = () => {
    setNum(count + 1);
  };

//   useEffect(() => {
//     alert("hai");
//   },[1]);
  return (
    <div className="container-fluid">
      <div className="header text-center display-6  text-bg-dark">
        Effect Example
      </div>
      <h1>{count}</h1>
      <button className="btn btn-primary mt-1" onDoubleClick={Incrise}>
        +
      </button>
    </div>
  );
};

export default UseEffect;
