import React from "react";
import { useState } from "react";

const Forms = () => {
  const [user, setUser] = useState({ Name: "", Age: "", Mobile: "", City: "" });

  let handleSubmitClick = () => {
    let handleNameChange = (e) => {
      setUser({
        Name: e.target.value,
        Age: user.Age,
        Mobile: user.Mobile,
        City: user.City,
      });
    };

    let handleAgeChange = (e) => {
      setUser({
        Name: user.Name,
        Age: e.target.value,
        Mobile: user.Mobile,
        City: user.City,
      });
    };

    let handleMobileChange = (e) => {
      setUser({
        Name: user.Name,
        Age: user.Age,
        Mobile: e.target.value,
        City: user.City,
      });
    };

    let handleCityChange = (e) => {
      setUser({
        Name: user.Name,
        Age: user.Age,
        Mobile: user.Mobile,
        City: e.target.value,
      });
    };
  };
  
  return (
    <div>
      <div className="container-fluid d-flex justify-content-center border border-5 border-danger w-50 mt-5 p-5 bg-black text-bg-danger text-center  ">
        <form onSubmit={handleSubmitClick}>
          <h3>Register User</h3>
          <dl>
            <dt>User Name</dt>
            <dd>
              <input type="text" onChange={handleNameChange} />
            </dd>
            <dt>Age</dt>
            <dd>
              <input type="number" onChange={handleAgeChange} />
            </dd>
            <dt>Mobile</dt>
            <dd>
              <input type="text" onChange={handleMobileChange} />
            </dd>
            <dt>City</dt>
            <dd>
              <select onChange={handleCityChange}>
                <option value="-1">Select City</option>
                <option value="Delhi">Delhi</option>
                <option value="Hyd">Hyd</option>
              </select>
            </dd>
          </dl>
          <button>Submit</button>
          <h2 className="text-warning mt-5">{user.Name}</h2>
          <h2 className="text-warning ">{user.Age}</h2>
          <h2 className="text-warning ">{user.Mobile}</h2>
          <h2 className="text-warning ">{user.City}</h2>
        </form>
      </div>
    </div>
  );
};

export default Forms;
