import React, { useEffect, useState } from "react";

const UseEffect = () => {
  const [products, setProducts] = useState([
    {
      title: "",
      price: 0,
      ratting: { rate: 0, count: 0, reviews: 0 },
      features: [],
      photo: "",
    },
  ]);

  useEffect(() => {
    fetch("products.json")
      .then((res) => res.json())
      .then((products) => {
        setProducts(products);
      });
  });

  return (
    <>
      <div className="container-fluid">
        {products.map((product) => {
          return (
            <>
              <div className="row mt-4">
                <div className="col-3"><img src={product.photo} alt="" /></div>
              </div>
            </>
          );
        })}
      </div>
    </>
  );
};

export default UseEffect;

