import React from "react";

const UserLogin = () => {
  let loginPage = (e) => {
    e.preventDefault();
    console.log(e);
    console.log(e.target);

    let user = e.target[0];
    let pswd = e.target[1];

    if (user.value === "Sourav Kumar Dash" && pswd.value === "2002") {
      window.location = "Home.jsx";
    } else {
      let err = "border: 2px solid red";
      user.style.cssText = err;
      pswd.style.cssText = err;
      window.alert("Incorrect password");
    }
  };
  return (
    <div>
      <div class="main">
        <div class="Logo-flot">
          <h1>DashCode Platform</h1>
        </div>
        <div class="container">
          <div class="logo-container">
            <div class="box">
              <img
                src="./assets/page-logo.png"
                alt=""
                height="100%"
                width="100%"
              />
            </div>
          </div>
          <div class="login-container">
            <div class="form-container">
              <div class="logo">
                <img src="./assets/page-logo2.png" alt="" />
              </div>
              <div class="form">
                <form onSubmit={loginPage(even)}>
                  <input type="text" placeholder="Enter your number or Email" />
                  <input type="password" placeholder="Password" />
                  <button>Log in</button>
                </form>
              </div>
              <div>OR</div>
              <div class="log-fb">Login in with Facebook</div>
              <div>
                <a href="/">forgot password?</a>
              </div>
            </div>
            <div class="sign-up">
              <div>
                Don't have an account- <a href="/">Sign up</a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
