import "./login.css";
import { useState } from "react";

export function Login() {
  const [msg, setMsg] = useState("");
  const [pass , passerr] =use

  function NameBlur(e) {
    if (e.target.value === "") {
      setMsg("User Name Required");
    } else {
      setMsg("");
    }
  }
  function PassBlur(e){
    if (e.target.value === "") {
      setMsg("User Name Required");
    } else {
      setMsg("");
    }
  }

  let loginPage = (e) => {
    e.preventDefault();

    let user = e.target[0];
    let pswd = e.target[1];

    if (user.value === "Sourav Kumar Dash" && pswd.value === "2002") {



      window.location = "UserDetails";
    } else if (user.value === "Jagannath Mallik" && pswd.value === "2001") {
      // alert(`User id : ${user.value} Account Number :3200353677  `);
      window.location = "User2";
    } else if (user.value === "jhon" && pswd.value === "2000") {
      window.location = "User3";
      // alert(`User id : ${user.value} Account Number :3200353677  `);
    } else {
      let err = "border: 2px solid red";
      user.style.cssText = err;
      pswd.style.cssText = err;
      window.alert(`Incorrect password`);
    }
  };

  return (
    <div className="container" onSubmit={loginPage}>
      <form>
        <h2>User Login</h2>
        <dl>
          <dt>Customer Id</dt>
          <dd>
            <input onBlur={NameBlur} type="text" placeholder="User ID" />
            <dd className="text-danger">{msg}</dd>
          </dd>
          <dt>Password</dt>
          <dd>
            <input onBlur={PassBlur}  type="password" placeholder="Password" />
            <dd className="text-danger">{msg}</dd>
          </dd>
        </dl>
        <button className="btn btn-danger login">Login</button>
      </form>
    </div>
  );
}
